import React from 'react';
import '../style/Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PageNavbar from './PageNavbar';
import ZipButton from './ZipButton';
import AnalyticsRow from './AnalyticsRow';

export default class Analytics extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component. This component maintains the list of genres,
    // and a list of movies for a specified genre.
    this.state = {
      genres: [],
      zips: []
    }

    this.showAveragePricePerZipcode = this.showAveragePricePerZipcode.bind(this);
  }

  // React function that is called when the page load.
  componentDidMount() {
    // Send an HTTP request to the server.
    /*fetch("http://localhost:8081/genres", {
      method: 'GET' // The type of HTTP request.
    })
      .then(res => res.json()) // Convert the response data to a JSON.
      .then(genreList => {
        if (!genreList) return;
        // Map each genreObj in genreList to an HTML element:
        // A button which triggers the showMovies function for each genre.
        let genreDivs = genreList.map((genreObj, i) =>
          <GenreButton key={i} id={"button-" + genreObj.genre} onClick={() => this.showMovies(genreObj.genre)} genre={genreObj.genre} />
        );

        // Set the state of the genres list to the value returned by the HTTP response from the server.
        this.setState({
          genres: genreDivs
        })
      })
      .catch(err => console.log(err))	// Print the error if there is one.*/
      <ZipButton key={0} id={"zips"} onClick={() => this.showAveragePricePerZipcode()} />
    }


  /* ---- Q1b (Dashboard) ---- */
  /* Set this.state.movies to a list of <DashboardMovieRow />'s. */
  showAveragePricePerZipcode() {
    fetch("http://localhost:8081/getAvgPricePerZipcode", {
			method: "GET"
		})
			.then(res => res.json())
			.then(zipsList => {
				console.log(zipsList);
				let zipsDivs = zipsList.map((zip, i) =>
        <AnalyticsRow key={i} zipcode={zip[0]} avgprice={zip[1]}/>
        );
          this.setState({
            zips: zipsDivs
          });
        })
        .catch(err => console.log(err))
    }

  render() {
    return (
      <div className="Analytics">

        <PageNavbar active="analytics" />

        <br></br>
        <div className="container movies-container">
          <div className="jumbotron">
            <div className="h5">Average Price Per ZipCode</div>
          </div>

          <br></br>
          <div className="jumbotron">
            <div className="movies-container">
              <div className="movies-header">
                <div className="header-lg"><strong>Zipcode</strong></div>
                <div className="header"><strong>Average Price</strong></div>
              </div>
              <div className="results-container" id="results">
                {this.state.zips}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
